using FluentAssertions;
using Sitecore.Horizon.Integration.Diagnostics;
using Sitecore.Horizon.Integration.Media.Models;
using Sitecore.Horizon.Integration.Media.Validators;
using Sitecore.Horizon.Integration.Tests.Unit.AutoFixture;
using Xunit;

namespace Sitecore.Horizon.Integration.Tests.Unit.Media.Validators
{
    public class ValidateMediaFileSizeTests
    {
        [Theory]
        [InlineAutoNData("iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAQAAAC1HAwCAAAAC0lEQVR42mNk+A8AAQUBAScY42YAAAAASUVORK5CYII=", true, MediaErrorCode.None)]
        [InlineAutoNData("", true, MediaErrorCode.None)]
        [InlineAutoNData("", false, MediaErrorCode.EmptyFile)]
        internal void IsValid_ShouldValidateSize(string base64String, bool isValid, MediaErrorCode code, ValidateMediaFileSize sut, UploadMediaModel uploadMedia)
        {
            // arrange
            uploadMedia.Blob = System.Convert.FromBase64String(base64String);

            // act
            var result = sut.IsValid(uploadMedia);

            // assert
            result.Should().Be(isValid);
            sut.ErrorCode.Should().Be(code);
        }
    }
}
